export const t = (key: string, lang: Language, options?: Record<string, string | number>): string => {
  const translationSet = (translations as any)[key];

  if (!translationSet) {
    return key;
  }

  let translation = translationSet[lang] || translationSet[Language.EN];

  if (typeof translation === 'string' && options) {
    for (const [optionKey, optionValue] of Object.entries(options)) {
      const regex = new RegExp(`{${optionKey}}`, 'g');
      translation = translation.replace(regex, optionValue.toString());
    }
  }

  return translation || key;
};
